# Dashboard Compatibility Fix for All Engines\n# Add missing attributes for dashboard visibility\n\nclass EngineCompatibility:\n    @staticmethod\n    def make_engines_dashboard_visible():\n        # Import all engine modules to ensure they're loaded\n        engine_modules = [\n            'app.engines.ml_engine', 'app.engines.fusion_engine', 'app.engines.quantum_engine',\n            'engines.security_engine', 'engines.model_monitoring_engine', 'engines.data_quality_engine',\n            'engines.explainability_engine', 'analytics.bigdata.local_analytics_engine'\n        ]\n        \n        for module_path in engine_modules:\n            try:\n                __import__(module_path)\n                print(f\"✓ {module_path} loaded for dashboard\")\n            except ImportError as e:\n                print(f\"⚠ {module_path}: {e}\")\n\n# Execute the fix\nEngineCompatibility.make_engines_dashboard_visible()\n